      "data": {
        "headline": "Success Stories",
        "subheadline": "How Clients Found Their Solutions",
        "items": {
          "work1": {
            "headline": "From Idea to Audience",
            "description": "A tech entrepreneur used PageBlitz to test an innovative app idea. Within weeks, they built a mailing list of over 500 interested users, refining their product to meet real demands."
          },
          "work2": {
            "headline": "Empowering Creatives",
            "description": "An artist launched a campaign for their new portfolio. With eye-catching landing pages created on PageBlitz, they quickly attracted collaborators and opportunities."
